import React, { useState, useEffect } from "react";
import isPrimary from "../functions/primary";
import { v4 as uuid } from "uuid";

function Row({ num_of_seats,row_num,setSeatSelected,setTotalCost }) {
  const [seats, setSeats] = useState([]);

  useEffect(() => {
    let num_seats = [
      ...[...Array(num_of_seats).keys()].map((item) => item + 1),
    ];
    setSeats(num_seats);
  }, [num_of_seats]);
  const clickHandler = (e)=>{
      setSeatSelected((prev)=>{
        return prev+1
      })
      setTotalCost((prev)=>{
        return prev + (row_num*10)
      })
      e.target.disabled = true
      console.log(e.target.disabled)
      console.log(e)
  }

  return (
    <React.Fragment>
      <div className="row">
      ROW -{ row_num}
        {seats.map((item, index) => {
          return (
            <button onClick={clickHandler} key={uuid()} disabled={isPrimary(item)}>
              {isPrimary(item)?"Booked":`Not Booked ${index+1}`}
            </button>
          );
        })}
      </div>
      
    </React.Fragment>
  );
}

export default Row;
