import React, { useState } from "react";
import { useParams } from "react-router-dom";
import {v4 as uuid} from "uuid";
import Row from "../components/Row";
import {useNavigate} from "react-router-dom"
function Booking({setTotalCost}) {
    const navigate = useNavigate()
  const { rows } = useParams();
  const [error, setError] = useState("");
  const [seats,setSeats] = useState([])
  const [seatSelected, setSeatSelected] = useState(0)
  //fetching data from the api
  //   React.useEffect(() => {
  //     fetch(`https://codebuddy.review/seats?count=${rows}`)
  //       .then((res) => res.json())
  //       .then((data) => {
  //         console.log(data);
  //       });
  //   }, [rows]);

  //checking number of seats

  React.useEffect(() => {
      let num_row = Number(rows)
      if(num_row<3 || num_row>10){
          setError("The maximum number of rows will be 10 and mimimum number of rows will be 3.")
      }

     let num_seats = [...[...Array(num_row).keys()].map(item=>item+1)].reverse()
     setSeats(num_seats)
  }, [rows]);
  React.useEffect(()=>{
    console.log(seatSelected)
    
  },[seatSelected])
  return <React.Fragment>
      <div>
          {error? <p>{error}</p> :<>
            {
                seats.map((item,index)=>{
                    return <Row num_of_seats={item} row_num={item} setTotalCost={setTotalCost} setSeatSelected={setSeatSelected} key={uuid()} />
                })
            }
          </>}
      </div>
      <button onClick={()=>{navigate("/payment")}} className="main">Go To Payment Page</button>
  </React.Fragment>;
}

export default Booking;
