import React, { useState,useEffect } from "react";
import isPrimary from "../functions/primary";
import {v4 as uuid} from "uuid";

function Row({ num_of_seats }) {
  const [seats, setSeats] = useState([]);
  useEffect(()=>{
    let num_seats = [...[...Array(num_of_seats).keys()].map(item=>item+1)]
    setSeats(num_seats)
  },[num_of_seats])
  return (
    <React.Fragment>
      <div className="row">
          {
              seats.map((item,index)=>{
                  return <button key={uuid()} disabled={isPrimary(item)}>{item}</button>
              })
          }
      </div>
    </React.Fragment>
  );
}

export default Row;
