import React, { useState, useEffect } from "react";
import isPrimary from "../functions/primary";
import { v4 as uuid } from "uuid";

function Row({ num_of_seats,row_num,setSeatSelected,setTotalCost }) {
  const [seats, setSeats] = useState([]);
  const [selected,setSelected] = useState([])
  useEffect(() => {
    let num_seats = [
      ...[...Array(num_of_seats).keys()].map((item) => item + 1),
    ];
    setSeats(num_seats);
  }, [num_of_seats]);
  const clickHandler = (num)=>{
      setSeatSelected((prev)=>{
        return prev+1
      })
      setTotalCost((prev)=>{
        return prev + (row_num*10)
      })
     setSelected((prev)=>{
       return [...prev,num]
     })
  }
useEffect(()=>{
  console.log(selected)
},[selected])
  return (
    <React.Fragment>
      <div className="row">
      ROW -{ row_num}
        {seats.map((item) => {
          return (
            <button id={`button${item}`} className={selected.includes(item)?"selected":""}  onClick={()=>clickHandler(item)} key={uuid()} disabled={isPrimary(item)}>
              {isPrimary(item)?"Booked":`Not Booked ${item}`}
            </button>
          );
        })}
      </div>
      
    </React.Fragment>
  );
}

export default Row;
