import React,{useState} from 'react'

function Home() {
    const [seats,setSeats] = useState('')
    const clickHandler = (e)=>{
        e.preventDefault()
    }
    const onInputHandler = (e)=>{
        let value = e.target.value
        if(Number(value)>=3 && Number(value)<=10){
            console.log("Setting")
            setSeats(e.target.value)
        }
        else{
            e.target.value = seats
        }
    }
    return (
        <React.Fragment>
            <form>
                <input type="text" name="row" id="row_num" value={seats} onInput={onInputHandler} placeholder='Enter The Number Of Rows'/>
                <button type='submit' onClick={clickHandler}>Get Sets</button>
            </form>
        </React.Fragment>
    )
}

export default Home
